mapper.select:
SELECT


,dianyuan.dianyuan_name as dianyuanName
,dianyuan.dianyuan_phone as dianyuanPhone
,dianyuan.dianyuan_email as dianyuanEmail
,dianyuan.dianyuan_types as dianyuanTypes
,dianyuan.dianyuan_content as dianyuanContent
,dianyuan.dianyuan_delete as dianyuanDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.new_money as newMoney
,yonghu.yonghu_sum_jifen as yonghuSumJifen
,yonghu.yonghu_new_jifen as yonghuNewJifen
,yonghu.huiyuandengji_types as huiyuandengjiTypes
,users.username as uusername
,users.password as upassword
,users.role as urole
,users.addtime as uaddtime

FROM forum  a
left JOIN dianyuan dianyuan ON a.dianyuan_id = dianyuan.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
left JOIN users users ON a.users_id = users.id



        and a.id in

        #{item}



        and a.forum_name like CONCAT('%',#{params.forumName},'%')


        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and (
            a.dianyuan_id = #{params.dianyuanId}
        )


        and (
            a.users_id = #{params.usersId}
        )


        and a.forum_content like CONCAT('%',#{params.forumContent},'%')


          and a.super_ids >= #{params.superIdsStart}   


          and a.super_ids <= #{params.superIdsEnd}   


        and a.super_ids = #{params.superIds}


        and a.forum_state_types = #{params.forumStateTypes}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


          and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


          and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 




        and a.dianyuan_id IS NOT NULL


        and dianyuan.dianyuan_name like CONCAT('%',#{params.dianyuanName},'%')


        and dianyuan.dianyuan_phone like CONCAT('%',#{params.dianyuanPhone},'%')


        and dianyuan.dianyuan_email like CONCAT('%',#{params.dianyuanEmail},'%')


        and dianyuan.dianyuan_types = #{params.dianyuanTypes}



        and dianyuan.dianyuan_content like CONCAT('%',#{params.dianyuanContent},'%')


          and dianyuan.dianyuan_delete >= #{params.dianyuanDeleteStart}   


          and dianyuan.dianyuan_delete <= #{params.dianyuanDeleteEnd}   


        and dianyuan.dianyuan_delete = #{params.dianyuanDelete}



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.new_money >= #{params.newMoneyStart}   


          and yonghu.new_money <= #{params.newMoneyEnd}   


          and yonghu.yonghu_sum_jifen >= #{params.yonghuSumJifenStart}   


          and yonghu.yonghu_sum_jifen <= #{params.yonghuSumJifenEnd}   


          and yonghu.yonghu_new_jifen >= #{params.yonghuNewJifenStart}   


          and yonghu.yonghu_new_jifen <= #{params.yonghuNewJifenEnd}   


        and yonghu.huiyuandengji_types = #{params.huiyuandengjiTypes}




        and a.users_id IS NOT NULL


        and users.username like CONCAT('%',#{params.username},'%')


        and users.password like CONCAT('%',#{params.password},'%')


        and users.role like CONCAT('%',#{params.role},'%')


          and UNIX_TIMESTAMP(users.addtime) >= UNIX_TIMESTAMP(#{params.addtimeStart}) 


          and UNIX_TIMESTAMP(users.addtime) <= UNIX_TIMESTAMP(#{params.addtimeEnd}) 


order by a.${params.sort} ${params.order}
